mapper.update:
UPDATE wms_ware_sku
SET stock_locked = stock_locked + #{num}
WHERE
    sku_id = #{skuId}
  AND ware_id = #{wareId}
  AND stock - stock_locked > 0

UPDATE wms_ware_sku
SET stock_locked = stock_locked - #{skuNum}
WHERE
    sku_id = ${skuId}
  AND ware_id = #{wareId}

UPDATE wms_ware_sku
SET stock_locked = stock_locked + #{seckillCount}
WHERE
    sku_id = #{skuId}
  AND ware_id = #{wareId}
  AND stock - stock_locked >= #{seckillCount}
